mapper.select:
 select count(*) from company_info
where 1=1

  and company_status=#{companyStatus}

select a.*, b.key_name as companyStatusName, c.key_name as companyTypeName, d.key_name as companySizeName from company_info a left join dics_info b on a.company_status=b.key_value and b.dics_code='STATE' left join dics_info c on a.company_type=c.key_value and c.dics_code='CTYPE' left join dics_info d on a.company_size=d.key_value and d.dics_code='CSIZE' where 1=1
and a.company_name like concat ('%',#{companyName},'%')
and a.company_status=#{companyStatus}
and a.company_status!=#{aud}

mapper.insert:

insert into company_info (company_id, company_name, company_type, 
  company_size, company_status, opr, 
  opr_time, other, others,code
  )
values (#{companyId,jdbcType=INTEGER}, #{companyName,jdbcType=VARCHAR}, #{companyType,jdbcType=INTEGER}, 
  #{companySize,jdbcType=INTEGER}, #{companyStatus,jdbcType=INTEGER}, #{opr,jdbcType=INTEGER}, 
  #{oprTime,jdbcType=TIMESTAMP}, #{other,jdbcType=VARCHAR}, #{others,jdbcType=VARCHAR},#{code,jdbcType=VARCHAR}
  )
