mapper.select:
select a.*, b.talents_name as targetName, b.talents_resume as talentsResume, b.others as talentsImg, c.talents_name as userName, d.task_name as taskName, d.task_des as taskDes, d.other as taskImg, d.task_id as taskId, f.key_name as taskStatusName, g.talents_name as taskPublisherName from favorites_info a left join talents_info b on a.target_code=b.code left join talents_info c on a.user_code=c.code left join task_info d on a.task_code=d.code left join dics_info f on d.task_status=f.key_value and f.dics_code='TASKSTATE' left join talents_info g on d.task_publisher=g.code where 1=1
and a.user_code=#{userCode}
and a.fav_type=#{favType}
and a.task_code=#{taskCode}
order by a.opr_time desc

mapper.insert:

insert into favorites_info (fav_code, user_code, fav_type, 
  target_code, task_code, opr_time,code
  )
values (#{favId,jdbcType=INTEGER}, #{userCode,jdbcType=VARCHAR}, #{favType,jdbcType=INTEGER},
  #{targetCode,jdbcType=VARCHAR}, #{taskCode,jdbcType=VARCHAR}, #{oprTime,jdbcType=TIMESTAMP} ,#{code,jdbcType=VARCHAR}
  )
