mapper.select:
 select count(*) from order_info
where 1=1

  and order_status=#{orderStatus}

select a.*, b.talents_name as orderPartyAName, c.talents_name as orderPartyBName, d.key_name as orderStatusName from order_info a left join talents_info b on a.order_partyA=b.code left join talents_info c on a.order_partyB=c.code left join dics_info d on a.order_status=d.key_value and d.dics_code='STATE' where 1=1
and a.order_status=#{orderStatus}
and a.order_status!=#{aud}
and (a.order_partyA=#{my} or a.order_partyB=#{my})
and a.order_partyA=#{orderPartyA}
and a.order_partyB=#{orderPartyB}
order by a.order_start desc
